import React from "react";
import Header from "./components/Header";
import { Query } from '@apollo/react-components';
import { useQuery, gql } from "@apollo/client";



const get_Category = gql`
query{
categories{
products{
  prices{
    amount
  }
gallery
name
description
}
}
}
  `;


function App () {


  const { data } = useQuery(get_Category);
  console.log({ data });

    return (
      <div className="App">
          <Query query={get_Category}>
        {this.props?.categories.map(category =>{
          return (
            <p>{category.name}</p>
          )
        })}
      </Query>
        {data?.categories.map((category) => {
          return (
            category.products.map((product, pIndex) => {
              return (
                <div className="product" key={pIndex}>

                  <div> {product.name} </div>

                  {product.gallery.map((imageUrl, gIndex) => {
                    return (
                      <img key={gIndex} className="image" src={imageUrl} alt="f" />
                    )
                  })}
                  {
                    product.prices.map((price, aIndex) => {
                      return (
                        <p key={aIndex}>{price.amount}</p>
                      )
                    })
                  }


                  {/* <div dangerouslySetInnerHTML={{ __html: product.description }}></div> */}

                </div>
              )
            })
          )
        })}

      </div>
    );
  }





export default App;
