import React, { Component } from 'react'
import { gql } from '@apollo/client';
import { Query } from '@'

const get_Category = gql`
query{
categories{
name
}
  }
  `;

class Header extends Component {

  

  render() {
    
    
    return (
      <div>Header
        <Query query={get_Category}>
        {this.props.data?.categories.map(category =>{
          return (
            <p>{category.name}</p>
          )
        })}
        </Query>
      </div>
    )
  }
}
export default Header;
