import React, { Component } from 'react'
import { gql } from '@apollo/client';



const get_Category = gql`
query{
categories{
name
}
  }
  `;

class Header extends Component {

  

  render() {
    
    
    return (
      <div>
        <Query query={get_Category}>
        {this.props?.categories.map(category =>{
          return (
            <p>{category.name}</p>
          )
        })}
      </Query>
      </div>
    )
  }
}
export default Header;
