import { useQuery, gql } from "@apollo/client";

const get_Category = gql`
query{
categories{
products{
gallery
name
description
}
}
}
  `;
function Header() {
    
  const { data } = useQuery(get_Category);
  console.log({ data });
  return (
    <div>Header</div>
  )
}

export default Header
