import React from "react";
import { useQuery, gql } from "@apollo/client";



const get_Category = gql`
query{
categories{
products{
gallery
}
}
}
  `;


function App() {

  const { data } = useQuery(get_Category);
  console.log({ data });




  return (
    <div className="App">
      {data.categories.map((category) => {
        return (
          category.products.map((product) => {
            return (
              product.gallery.map((imageUrl, index) => {
                return(
                  <img className="image" key={index}  src={imageUrl} alt="f" />
                )

              })
            )
          })
        )
      })}

    <h1>Hoku</h1>
    </div>
  );
}

export default App;
